import React from 'react';
import { Button } from './Button.js';
import '../App.css';
import './Panel.css';
import $ from 'jquery';

const PanelMain = (props) => {
    $(document).ready(function() {
        $('.panel').mouseover(function() {
            $(this).find('.panel__item').css('transform', 'scale(1.1)');
        });
        $('.panel').mouseout(function() {
            $(this).find('.panel__item').css('transform', 'scale(1)');
        });
    });

    return (
        <div className='panel__container'>
            <div className='panel'>
                <div className='panel__item' style={{ backgroundImage: `url(${ props.background })` }}></div>
                <div className='panel__item__content'>
                    <h1>{ props.title }</h1>
                    <p>{ props.description }</p>
                    <div className='hero__btns'>
                        <Button
                            className='btn'
                            buttonStyle='btn--outline'
                            buttonSize='btn--large'
                        >
                            { props.button }
                        </Button>
                    </div>
                </div>
            </div>
        </div>
    );
}

const PanelDivided = (props) => {
    $(document).ready(function() {
        $('.panel').mouseover(function() {
            $(this).find('.panel__item').css('transform', 'scale(1.1)');
        });
        $('.panel').mouseout(function() {
            $(this).find('.panel__item').css('transform', 'scale(1)');
        });
    });

    return (
        <div className='panel__container'>
            <div className='panel'>
                <div className='panel__item'>
                    <div className='panel__item__content'>
                        <h1>{props.title}</h1>
                        <p>{props.description}</p>
                        <div className='hero__btns'>
                            <Button
                                className='btn'
                                buttonStyle='btn--outline'
                                buttonSize='btn--large'
                            >
                                {props.button}
                            </Button>
                        </div>
                    </div>
                </div>
                <div className='panel__item' style={{ backgroundImage: `url(${ props.background })` }}></div>
            </div>
        </div>
    );
}

const PanelCards = (props) => {
    $(document).ready(function() {
        $('.panel').mouseover(function() {
            $(this).find('.panel__item').css('transform', 'scale(1.1)');
        });
        $('.panel').mouseout(function() {
            $(this).find('.panel__item').css('transform', 'scale(1)');
        });
    });

    return (
        <div className='panel__container'>
            <div className='panel'>
                <div className='panel__item' style={{ backgroundImage: `url(${ props.background })` }}></div>
                <div className='panel__item__content'>
                    <h1>{ props.title }</h1>
                    <p>{ props.description }</p>
                    <div className='hero__btns'>
                        <Button
                            className='btn'
                            buttonStyle='btn--outline'
                            buttonSize='btn--large'
                        >
                            { props.button }
                        </Button>
                    </div>
                </div>
            </div>
        </div>
    );
}

const Panel = (props) => {
    const panelSettings = (props) => ({
        main: <PanelMain {...props} />,
        divided: <PanelDivided {...props} />,
        cards: <PanelCards {...props} />
    });

    return (
        <>
            {panelSettings(props)[props.style]}
        </>
    );
}

export default Panel;
