import React from 'react';
import { Button } from './Button.js';
import '../App.css';
import './Panel.css';
import $ from 'jquery';

const Panel = (props) => {
    $(document).ready(function() {
        $('.panel').mouseover(function() {
            $(this).find('.panel__item').css('transform', 'scale(1.1)');
        });
        $('.panel').mouseout(function() {
            $(this).find('.panel__item').css('transform', 'scale(1)');
        });
    });


    return (
        <div className='panel__container'>
            <div className='panel'>
                <div className='panel__item' style={{ backgroundImage: `url(${props.background})` }}></div>
                <div className='panel__item__content'>
                    <h1>{ props.title }</h1>
                    <p>{ props.description }</p>
                    <div className='hero__btns'>
                        <Button
                            className='btn'
                            buttonStyle='btn--outline'
                            buttonSize='btn--large'
                        >
                            { props.button }
                        </Button>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Panel;
